package com.gmo.phuongbt;

public class DataTypeVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// data type - boolean
		boolean testResult = true;
		testResult = false;
		System.out.println("Test Result is " + testResult);
		testResult = true;
		System.out.println("Test Result is " + testResult);
		// data type = int
		int carSpeed = 20;
		System.out.println("The speed of the car is " + carSpeed);
		carSpeed += 20;
		System.out.println("The current speed of the car is " + carSpeed);
		// Data Type 遯ｶ�ｿｽ char
		char ch = 'P';
		System.out.println("Value of the char is " + ch);
		// Data Type 遯ｶ�ｿｽ double
		double so_PI = 3.14159;
		System.out.println("PI" + so_PI);

	}
}
